import type { TSchema, SchemaOptions } from '@sinclair/typebox/build/require/type/schema';
import type { Evaluate } from '@sinclair/typebox/build/require/type/helpers';
import type { TMappedResult } from '@sinclair/typebox/build/require/type/mapped';
import { type TReadonlyOptional } from '@sinclair/typebox/build/require/type/readonly-optional';
import { type TOptional } from '@sinclair/typebox/build/require/type/optional';
import { type TReadonly } from '@sinclair/typebox/build/require/type/readonly';
import { type TRecursive } from '@sinclair/typebox/build/require/type/recursive';
import { type TIntersect } from '@sinclair/typebox/build/require/type/intersect';
import { type TUnion } from '@sinclair/typebox/build/require/type/union';
import { type TObject, type TProperties } from '@sinclair/typebox/build/require/type/object';
import { type TRequiredFromMappedResult } from '@sinclair/typebox/build/require/type/required/required-from-mapped-result';
type TFromRest<T extends TSchema[], Acc extends TSchema[] = []> = (T extends [infer L extends TSchema, ...infer R extends TSchema[]] ? TFromRest<R, [...Acc, TRequired<L>]> : Acc);
type TFromProperties<T extends TProperties> = Evaluate<{
    [K in keyof T]: T[K] extends (TReadonlyOptional<infer S>) ? TReadonly<S> : T[K] extends (TReadonly<infer S>) ? TReadonly<S> : T[K] extends (TOptional<infer S>) ? S : T[K];
}>;
export type TRequired<T extends TSchema> = (T extends TRecursive<infer S> ? TRecursive<TRequired<S>> : T extends TIntersect<infer S> ? TIntersect<TFromRest<S>> : T extends TUnion<infer S> ? TUnion<TFromRest<S>> : T extends TObject<infer S> ? TObject<TFromProperties<S>> : TObject<{}>);
/** `[Json]` Constructs a type where all properties are required */
export declare function Required<T extends TMappedResult>(T: T, options?: SchemaOptions): TRequiredFromMappedResult<T>;
/** `[Json]` Constructs a type where all properties are required */
export declare function Required<T extends TSchema>(T: T, options?: SchemaOptions): TRequired<T>;
export {};
